import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Form, Button, Spinner } from 'react-bootstrap';
import { reportAPI, statsAPI } from '../services/api';
import { Bar, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const AuthorityDashboard = () => {
  const [reports, setReports] = useState([]);
  const [statistics, setStatistics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    problem_type: '',
    priority: ''
  });

  useEffect(() => {
    fetchReports();
    fetchStatistics();
  }, [filters]);

  const fetchReports = async () => {
    try {
      const response = await reportAPI.getReports(filters);
      setReports(response.data.reports);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  const fetchStatistics = async () => {
    try {
      const response = await statsAPI.getStatistics();
      setStatistics(response.data);
    } catch (error) {
      console.error('Error fetching statistics:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (referenceId, newStatus) => {
    try {
      await reportAPI.updateStatus(referenceId, { status: newStatus });
      fetchReports();
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      submitted: 'warning',
      in_process: 'info',
      resolved: 'success'
    };
    return <Badge bg={variants[status]}>{status.replace('_', ' ').toUpperCase()}</Badge>;
  };

  const getPriorityBadge = (priority) => {
    const variants = {
      low: 'success',
      medium: 'warning',
      high: 'danger'
    };
    return <Badge bg={variants[priority]}>{priority.toUpperCase()}</Badge>;
  };

  if (loading) {
    return (
      <Container className="text-center py-5">
        <Spinner animation="border" />
        <p>Loading dashboard...</p>
      </Container>
    );
  }

  const chartData = {
    labels: statistics?.reports_by_type?.map(item => item._id) || [],
    datasets: [
      {
        label: 'Reports by Type',
        data: statistics?.reports_by_type?.map(item => item.count) || [],
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
    ],
  };

  return (
    <Container fluid className="py-4">
      <h2 className="mb-4">Park Authority Dashboard</h2>
      
      {/* Statistics Cards */}
      <Row className="mb-4">
        <Col md={3}>
          <Card className="text-white bg-primary">
            <Card.Body>
              <h5>Total Reports</h5>
              <h2>{statistics?.total_reports || 0}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-success">
            <Card.Body>
              <h5>Resolved</h5>
              <h2>{statistics?.resolved_reports || 0}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-info">
            <Card.Body>
              <h5>In Progress</h5>
              <h2>{statistics?.in_progress_reports || 0}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-warning">
            <Card.Body>
              <h5>Resolution Rate</h5>
              <h2>{statistics?.resolution_rate?.toFixed(1) || 0}%</h2>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row className="mb-4">
        <Col md={6}>
          <Card>
            <Card.Body>
              <h5>Reports by Problem Type</h5>
              <Bar data={chartData} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card>
            <Card.Body>
              <h5>Weekly Report Trend</h5>
              {/* Add line chart here */}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Filters */}
      <Card className="mb-4">
        <Card.Body>
          <Row>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Status</Form.Label>
                <Form.Select
                  value={filters.status}
                  onChange={(e) => setFilters({...filters, status: e.target.value})}
                >
                  <option value="">All Status</option>
                  <option value="submitted">Submitted</option>
                  <option value="in_process">In Process</option>
                  <option value="resolved">Resolved</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Problem Type</Form.Label>
                <Form.Select
                  value={filters.problem_type}
                  onChange={(e) => setFilters({...filters, problem_type: e.target.value})}
                >
                  <option value="">All Types</option>
                  <option value="litter">Litter</option>
                  <option value="damage">Damage</option>
                  <option value="safety">Safety</option>
                  <option value="maintenance">Maintenance</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Priority</Form.Label>
                <Form.Select
                  value={filters.priority}
                  onChange={(e) => setFilters({...filters, priority: e.target.value})}
                >
                  <option value="">All Priorities</option>
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3} className="d-flex align-items-end">
              <Button variant="secondary" onClick={() => setFilters({})}>
                Clear Filters
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {/* Reports Table */}
      <Card>
        <Card.Body>
          <h5>Recent Reports</h5>
          <div className="table-responsive">
            <Table hover>
              <thead>
                <tr>
                  <th>Reference ID</th>
                  <th>Type</th>
                  <th>Description</th>
                  <th>Priority</th>
                  <th>Status</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {reports.map((report) => (
                  <tr key={report._id}>
                    <td><code>{report.reference_id}</code></td>
                    <td>{report.problem_type}</td>
                    <td>{report.description.substring(0, 50)}...</td>
                    <td>{getPriorityBadge(report.priority)}</td>
                    <td>{getStatusBadge(report.status)}</td>
                    <td>{new Date(report.created_at).toLocaleDateString()}</td>
                    <td>
                      <div className="d-flex gap-2">
                        <Button
                          size="sm"
                          variant="outline-primary"
                          onClick={() => handleStatusUpdate(report.reference_id, 'in_process')}
                          disabled={report.status !== 'submitted'}
                        >
                          Start Process
                        </Button>
                        <Button
                          size="sm"
                          variant="outline-success"
                          onClick={() => handleStatusUpdate(report.reference_id, 'resolved')}
                          disabled={report.status === 'resolved'}
                        >
                          Mark Resolved
                        </Button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default AuthorityDashboard;
