import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle token expiration
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const authAPI = {
  register: (userData) => api.post('/register', userData),
  login: (credentials) => api.post('/login', credentials),
  verify2FA: (data) => api.post('/verify-2fa', data),
  enable2FA: () => api.post('/enable-2fa'),
};

export const reportAPI = {
  createReport: (formData) => api.post('/reports', formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  }),
  getReports: (params) => api.get('/reports', { params }),
  getReport: (referenceId) => api.get(`/reports/${referenceId}`),
  updateStatus: (referenceId, data) => api.put(`/reports/${referenceId}/status`, data),
};

export const statsAPI = {
  getStatistics: () => api.get('/statistics'),
  generateQR: (location) => api.get('/generate-qr', { 
    params: { location },
    responseType: 'blob' 
  }),
};

export default api;
