import React, { useState, useEffect } from 'react';
import { Table, Badge, Button, Card, Container, Row, Col, Form } from 'react-bootstrap';

const AuthorityDashboard = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    problem_type: ''
  });

  useEffect(() => {
    // Mock data for now
    const mockReports = [
      {
        id: '1',
        reference_id: 'CMP-20240115-ABC123',
        user_email: 'visitor@test.com',
        problem_type: 'litter',
        description: 'Garbage bin overflowing near playground',
        status: 'submitted',
        priority: 'medium',
        created_at: '2024-01-15T10:30:00Z'
      },
      {
        id: '2',
        reference_id: 'CMP-20240115-DEF456',
        user_email: 'visitor@test.com',
        problem_type: 'damage',
        description: 'Broken swing in playground',
        status: 'in_process',
        priority: 'high',
        created_at: '2024-01-15T11:30:00Z'
      },
      {
        id: '3',
        reference_id: 'CMP-20240115-GHI789',
        user_email: 'visitor2@test.com',
        problem_type: 'safety',
        description: 'Uneven pathway near fountain',
        status: 'resolved',
        priority: 'low',
        created_at: '2024-01-14T09:15:00Z'
      }
    ];
    
    setReports(mockReports);
    setLoading(false);
  }, []);

  const handleStatusUpdate = (reportId, newStatus) => {
    setReports(reports.map(report => 
      report.id === reportId 
        ? { ...report, status: newStatus }
        : report
    ));
  };

  const getStatusBadge = (status) => {
    const variants = {
      submitted: 'warning',
      in_process: 'info',
      resolved: 'success'
    };
    return <Badge bg={variants[status]}>{status.replace('_', ' ').toUpperCase()}</Badge>;
  };

  const getPriorityBadge = (priority) => {
    const variants = {
      low: 'success',
      medium: 'warning',
      high: 'danger'
    };
    return <Badge bg={variants[priority]}>{priority.toUpperCase()}</Badge>;
  };

  const filteredReports = reports.filter(report => {
    if (filters.status && report.status !== filters.status) return false;
    if (filters.problem_type && report.problem_type !== filters.problem_type) return false;
    return true;
  });

  const stats = {
    total: reports.length,
    submitted: reports.filter(r => r.status === 'submitted').length,
    in_process: reports.filter(r => r.status === 'in_process').length,
    resolved: reports.filter(r => r.status === 'resolved').length
  };

  if (loading) {
    return (
      <Container className="text-center py-5">
        <p>Loading dashboard...</p>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4">
      <h2 className="mb-4">Park Authority Dashboard</h2>
      
      {/* Statistics Cards */}
      <Row className="mb-4">
        <Col md={3}>
          <Card className="text-white bg-primary">
            <Card.Body>
              <h5>Total Reports</h5>
              <h2>{stats.total}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-warning">
            <Card.Body>
              <h5>Submitted</h5>
              <h2>{stats.submitted}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-info">
            <Card.Body>
              <h5>In Process</h5>
              <h2>{stats.in_process}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-success">
            <Card.Body>
              <h5>Resolved</h5>
              <h2>{stats.resolved}</h2>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Filters */}
      <Card className="mb-4">
        <Card.Body>
          <Row>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Status</Form.Label>
                <Form.Select
                  value={filters.status}
                  onChange={(e) => setFilters({...filters, status: e.target.value})}
                >
                  <option value="">All Status</option>
                  <option value="submitted">Submitted</option>
                  <option value="in_process">In Process</option>
                  <option value="resolved">Resolved</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Problem Type</Form.Label>
                <Form.Select
                  value={filters.problem_type}
                  onChange={(e) => setFilters({...filters, problem_type: e.target.value})}
                >
                  <option value="">All Types</option>
                  <option value="litter">Litter</option>
                  <option value="damage">Damage</option>
                  <option value="safety">Safety</option>
                  <option value="maintenance">Maintenance</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3} className="d-flex align-items-end">
              <Button 
                variant="secondary" 
                onClick={() => setFilters({ status: '', problem_type: '' })}
              >
                Clear Filters
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {/* Reports Table */}
      <Card>
        <Card.Body>
          <h5>Reports ({filteredReports.length})</h5>
          <div className="table-responsive">
            <Table hover>
              <thead>
                <tr>
                  <th>Reference ID</th>
                  <th>User Email</th>
                  <th>Problem Type</th>
                  <th>Description</th>
                  <th>Priority</th>
                  <th>Status</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredReports.map((report) => (
                  <tr key={report.id}>
                    <td><code>{report.reference_id}</code></td>
                    <td>{report.user_email}</td>
                    <td>{report.problem_type}</td>
                    <td>{report.description}</td>
                    <td>{getPriorityBadge(report.priority)}</td>
                    <td>{getStatusBadge(report.status)}</td>
                    <td>{new Date(report.created_at).toLocaleDateString()}</td>
                    <td>
                      <div className="d-flex gap-2">
                        {report.status === 'submitted' && (
                          <Button
                            size="sm"
                            variant="outline-primary"
                            onClick={() => handleStatusUpdate(report.id, 'in_process')}
                          >
                            Start Process
                          </Button>
                        )}
                        {report.status === 'in_process' && (
                          <Button
                            size="sm"
                            variant="outline-success"
                            onClick={() => handleStatusUpdate(report.id, 'resolved')}
                          >
                            Mark Resolved
                          </Button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default AuthorityDashboard;
